import {React, useEffect, useState, useRef } from "react";
//googlemap
import GoogleMapReact from "google-map-react";
//components
import useCurrentLocation from "../../Hooks/currentPosition";
//styles
import {Style, BtnStyle} from './Form.styles'
import bikeimg from '../../images/bike.png'
import personimg from '../../images/person.png'


function handleApiLoaded(map, maps) {
	console.log(maps)
}


const options = {
	enableHighAccuracy: true,
	timeout: 1000 * 60 * 1, // 1 min
	maximumAge: 1000 * 60 * 60, // 1 hour
  };

export default function MapContainer(props) {
	const { location, err } = useCurrentLocation(options);
	const mapRef = useRef();
	const [zoom, setZoom] = useState(10);
	const [bounds, setBounds] = useState(null);
	const showBikesOrNot = props.bikes.getbike;
	var bikeArray = props.bikes.content;
	bikeArray =  bikeArray.slice(0, 200); // innan jag fixar med cluster
	const Marker = ({children}) => children;

	_distanceToMouse = (markerPos, mousePos, markerProps) => {
		const x = markerPos.x;
		// because of marker non symmetric,
		// we transform it central point to measure distance from marker circle center
		// you can change distance function to any other distance measure
		const y = markerPos.y - K_STICK_SIZE - K_CIRCLE_SIZE / 2;

		// and i want that hover probability on markers with text === 'A' be greater than others
		// so i tweak distance function (for example it's more likely to me that user click on 'A' marker)
		// another way is to decrease distance for 'A' marker
		// this is really visible on small zoom values or if there are a lot of markers on the map
		const distanceKoef = markerProps.text !== 'A' ? 1.5 : 1;

		// it's just a simple example, you can tweak distance function as you wish
		return distanceKoef * Math.sqrt((x - mousePos.x) * (x - mousePos.x) + (y - mousePos.y) * (y - mousePos.y));
	  }


	return (
		<Style>
			{location ? (
			<GoogleMapReact 
				bootstrapURLKeys={{key: process.env.REACT_APP_GOOGLE_MAPS_API_KEY}}
				defaultCenter={{ lat: location.latitude, lng: location.longitude}}
				defaultZoom={3}
				yesIWantToUseGoogleMapApiInternals
				onGoogleApiLoaded= {({map, maps}) => {

					handleApiLoaded(map, maps)
					mapRef.current = map;

				}}
				onChange={({zoom, bounds}) => {
					setZoom(zoom)
					setBounds([
						bounds.nw.lng,
						bounds.se.lat,
						bounds.se.lng,
						bounds.nw.lat
					])
				}}
				hoverDistance={K_CIRCLE_SIZE / 2}
				distanceToMouse={this._distanceToMouse}
			>
			{/* 1 Person */}
			<Marker 
				key={1} 
				lat={location.latitude} 
				lng={location.longitude}>	
				<BtnStyle>
					<button onClick= {() => {
						<div>HEJ</div>
						}}
						className="crime-marker">
						<img className = "scooter" src={personimg} alt="scooter"/>
					</button>
				</BtnStyle>
			</Marker>
			{/* Alla cyklar */}
			{showBikesOrNot ? (
			bikeArray.map(bike => {
			return (
			<Marker 
				key={bike.id} 
				lat={bike.location.latitude} 
				lng={bike.location.longitude}>	
				<BtnStyle>
					<button onClick= {() => {
						<p>HEJ</p>
						}}
						className="crime-marker">
						<img className = "scooter" src={bikeimg} alt="scooter"/>
					</button>
				</BtnStyle>
			</Marker>
			)
			})
			) : (console.log("HE"))}

			</GoogleMapReact>
			):(<p>Loading...</p> )}
		</Style>	

	);
}
