import {React, useEffect, useState } from "react";

//components
import Api from '../../api'
//styles & img
import {Container} from './Form.styles'
//other
var pointInPolygon = require('point-in-polygon');


const userInvoiceInitialValue = {
    tripId: "",
    endedAtChargePoint: "",
    endedAtLoadStation: "",
    timeOfTrip: "",
    startFee: 10,
    discountStartFee: "",
}


export default function Invoice(props) {
	//hooks
    // måste ha resan och koordinaterna
    // måste senare räkna ut var koordinaterna ligger och jmföra

    const [allCharging_posts, setAllCharging_posts] = useState([]) //parkering och laddstationer
    const [allParkingZones, setAllParkingZones] = useState([])
    const [userTrips, setUserTrips] = useState([]);
    const [userInvoice, setUserInvoice] = useState([userInvoiceInitialValue])
    /* Sparara alla ladda och parkeringszoner i state */

    console.log(props)

    useEffect(() => {
        const getAllCities = async () => {

        let allCities = await Api.getAllCitys();
        allCities.forEach(element => {
            setAllCharging_posts(element.charging_posts)
            setAllParkingZones(element.parking_zones)
            });
        }
        getAllCities()
        setUserTrips(props)

    }, [])


   /*  setAuthDocuments(prev => ({
        ...authDocs,
        fetchedAuthDoc:
            [...authDocs.fetchedAuthDoc]

    })); */


    function checkAllUsersTrips() {

        userTrips.props.forEach(trip => {
            var tripEnded = [trip.stop.position.lat, trip.stop.position.lng];

            setUserInvoice(prevState => ([{
                ...prevState,
				tripId: userTrips.props.id,
                endedAtChargePoint: "",
                endedAtLoadStation: "",
                timeOfTrip: "",
                startFee: 10,
                timeFee: "",
                discountStartFee: "",
            }]));

            // if (checkIfTripEndedAtParkingZone(tripEnded)) {
            //     console.log("HEJ")
            // }
        })

    }
            console.log(userInvoice)



    /* Kollar om resan slutade i en parkeringszon */
    function checkIfTripEndedAtParkingZone(tripEnded) {

        if(tripEnded) {
            let res;
            allParkingZones.forEach(element => {
                let polyGon=[
                    [element.position.polygonePart1.lat,
                    element.position.polygonePart1.lng],
                    [element.position.polygonePart2.lat,
                    element.position.polygonePart2.lng],
                    [element.position.polygonePart3.lat,
                    element.position.polygonePart3.lng],
                    [element.position.polygonePart4.lat,
                    element.position.polygonePart4.lng]
                ]
                return checkIfPointInPolyGon(tripEnded, polyGon);
            });
        }
    }

    function checkIfTripEndedAtChargingPost() {
        if(props && props.userHistory) {
            let res = false;
            var tripEnded = [props.userHistory.stop.position.lat, props.userHistory.stop.position.lng];
            allCharging_posts.forEach(element => {
                let polyGon=[
                    [ element.position.polygonePart1.lat, element.position.polygonePart1.lng],
                    [element.position.polygonePart2.lat, element.position.polygonePart2.lng],
                    [element.position.polygonePart3.lat, element.position.polygonePart3.lng],
                    [element.position.polygonePart4.lat, element.position.polygonePart4.lng ]
                ]
                res = checkIfPointInPolyGon(tripEnded, polyGon);
                console.log(res)
            });
        }
    }


    function checkIfPointInPolyGon(point, polygone) {
        return pointInPolygon(point, polygone);
    }




    async function calculateCost() {

        checkAllUsersTrips()
        /* checkIfTripEndedAtParkingZone()
        checkIfTripEndedAtChargingPost()  */
    }

	return (
        <button onClick={calculateCost}>Faktura</button>
	);
}
