import React, { Component } from "react";

import {ContainerStyle, style, Wrapper} from './Form.styles'

import {Map, InfoWindow, Marker, GoogleApiWrapper} from 'google-maps-react';




// style annorlunda jmf med komponenter. Se form.styles,  
export class MapContainer extends Component {

	state = {
		latitude: "",
		longitude: ""
	}

	componentDidMount() {
		const url =
		  'https://en.wikipedia.org/w/api.php?action=opensearch&search=Seona+Dancing&format=json&origin=*'

		fetch(url)
		  .then((result) => result.json())
		  .then((result) => {
			this.setState({
			  data: result,
			})
		  })
	  }

	render() {

		const {state} = this.state

		var options = {
		enableHighAccuracy: true,
		timeout: 5000,
		maximumAge: 0
		};

		function success(pos) {
		var crd = pos.coords;

		this.setState({
			latitude: crd.latitude,
			longitude: crd.longitude
		})
		console.log('Your current position is:');
		console.log(`Latitude : ${crd.latitude}`);
		console.log(`Longitude: ${crd.longitude}`);
		console.log(`More or less ${crd.accuracy} meters.`);
		}

		function error(err) {
		console.warn(`ERROR(${err.code}): ${err.message}`);
		}

		navigator.geolocation.getCurrentPosition(success, error, options);

		return (
		<Map 
			google={this.props.google} 
			zoom={14}
			containerStyle={style}
			initialCenter={{ // ändra beroende på vart användaren befinner sig
				lat: this.state.latitude,
				lng: this.state.longitude
			}}
		>
		<Marker onClick={this.onMarkerClick}
				name={'Current location'} />

		<InfoWindow onClose={this.onInfoWindowClose}>
			<div>
			<h1> {/* this.state.selectedPlace.name */} </h1>
			</div>
		</InfoWindow>
		</Map>
		);
	}
}

export default GoogleApiWrapper({
  apiKey: ("AIzaSyDJqw6CXS1UIpqXefge1iH1-uBzFbXDAH0")
})(MapContainer)


