import React, { useContext, useState, useEffect } from "react";
import { Link, Outlet, Navigate } from "react-router-dom";

//style
import { Wrapper, Logga, Nav } from './Form.styles.js';


//components
import SignOut from "../Logout/index.js";
import AppContext from "../../AppContext";



const Header = () => {

    let myContext = useContext(AppContext);
    const auth = myContext.useAuth();
    const [authState, setAuthState] = useState("");
    console.log("AUTH", auth)
    if (auth !== undefined) {
        setAuthState(auth)
    }

    useEffect(() => {
        // everything here stays the same as before
    }, [authState, auth]);

    const handleSubmit = async (event)  => {

        localStorage.clear();
        alert("Du är utloggad")
        setAuthState(false);
        event.preventDefault();
        <Navigate to="/welcome" />
    }



    console.log(authState, "hääär")


    return (
        <Wrapper>
            <Logga> Svenska Elsparkcyklar // LOGGA ? </Logga>
            {authState ? (
            <>
                <Nav>
                    <Link to="/invoices">Historik </Link> |{" "} {/* Link som global navigation */}
                    <Link to="/expenses">Konto</Link>
                </Nav>
                <SignOut>
                    <form onSubmit={handleSubmit} className = "logout">
                        <input type="submit" value="Logga ut" />
                    </form>
                </SignOut>

                <Outlet/> {/* outlet renderar child  */}
                </>
            ): console.log("FALSE") }
        </Wrapper>
    )
}

export default Header
