import {React, useState } from "react";

const HandleApiLoaded = (mapRef, props) => {
	if (props.content.length > 0 && mapRef.map !== null) {
		var cityname;
		const map = mapRef.map;
		const maps = mapRef.maps;

		props.content.map(coords =>
			{
			cityname = coords.city
			let polyGon = 
				([coords.position.polygonePart1,
					coords.position.polygonePart2,
					coords.position.polygonePart3,
					coords.position.polygonePart4
				]);


			if (props.getStations === true) {
				cityname = new maps.Polygon({
					paths: polyGon,
					strokeColor: "#FF0000",
					strokeOpacity: 0.8,
					strokeWeight: 2,
					fillColor: "#FF0000",
					fillOpacity: 0.35,
					});
				cityname.setMap(map);

				}

			})

		}
}
export default HandleApiLoaded



