import React, { useContext, useState, useEffect } from "react";
import { Link, Outlet, Redirect } from "react-router-dom";

//style
import { Wrapper, Logga, Nav, SignOut } from './Form.styles.js';

//components
import AppContext from "../../AppContext";

const Header = () => {

    const myContext = useContext(AppContext);
    const auth = myContext.auth;

    const handleSubmit = async (event)  => {
        myContext.toggleAuth(false);
        localStorage.clear();

        event.preventDefault();
    }


    return (
        <Wrapper>
            <Logga> Svenska Elsparkcyklar // LOGGA ? </Logga>
            {auth ? (
            <>
                <Nav>
                    <Link to="/invoices">Historik </Link> |{" "} {/* Link som global navigation */}
                    <Link to="/expenses">Konto</Link>
                </Nav>
                <SignOut>
                    <form onSubmit={handleSubmit} className = "logout">
                        <input type="submit" value="Logga ut" />
                    </form>
                </SignOut>

                <Outlet/> {/* outlet renderar child  */}
                </>
            ): console.log("FALSE") }
        </Wrapper>
    )
}

export default Header
