			// 				cityCoords.position.polygonePart3,
			// 				cityCoords.position.polygonePart4
			// 			]);
			// 		cityname = new maps.Polygon({
			// 			paths: polyGon,
			// 			strokeColor: "#FF0000",
			// 			strokeOpacity: 0.9,
			// 			strokeWeight: 2,
			// 			fillColor: "transparent",
			// 			fillOpacity: 0.35,
			// 			});

			// 		cityname.addListener('click', (event) => {
			// 			console.log("HEJ", cityCoords)
			// 			setPopupInfo({	
			// 				showBike: false,
			// 				showCity: true,
			// 				showLocation: false,
			// 				content: cityCoords
			// 			})
			// 		});
			// 		cityname.setMap(map);


			// 		setCitysObject(oldArray => [...oldArray, cityname]);
			// 		})
			// 	}

        } catch (error) {
			console.log(error)
        }
	}

	return (
		<Style>
			{/* 		KARTA		 */}
			{location ? (
			<GoogleMapReact 
				bootstrapURLKeys={{key: process.env.REACT_APP_GOOGLE_MAPS_API_KEY}}
				defaultCenter={{ lat: location.latitude, lng: location.longitude}}
				defaultZoom={10}
				yesIWantToUseGoogleMapApiInternals
				onGoogleApiLoaded= {({map, maps}) => {
					mapRef.current = {map, maps};
				}}
			>
			{/* 		1 Person 		*/}
			<Marker 
				key={1} 
				lat={location.latitude} 
				lng={location.longitude}
				>
				<div >
					<img className = "crime-marker" src={personimg} alt="person" />
				</div>	
			</Marker>
			{/* 		Alla cyklar		 */}
			{showBikesOrNot ? (
			bikeArray.map(bike => {
			return (
			<Marker 
				key={bike.id} 
				lat={bike.location.latitude} 
				lng={bike.location.longitude}
				>
				<div onClick={() => setPopupInfo({
					showBike: true,
					showCity: false,
					showLocation: false,
					content: bike
					})}className="crime-marker">
				<img className = "scooter" src={bikeimg} alt="scooter"/>
				</div>
			</Marker>)
			})
			) : (console.log("HE"))}


			</GoogleMapReact>
			):(<p>Loading...</p> )}

			{/*				 POPUP				 */}
			{popupInfo ? (
			<PopUp
				key={"popupkey"}
				store={popupInfo} 
				>
			</PopUp>
			): (console.log("EJ POPUP"))}
