import React, { useState, useEffect, useContext } from "react";
import { useNavigate } from "react-router-dom";
import {Wrapper} from "./Form.styles";
import { AppContext } from "../../AppContext";
import Api from "../../api";
import Loader from '../loader/loader'


require('dotenv').config()


export default function Login() {
  const [data, setData] = useState({ errorMessage: "", isLoading: false });
  const [loggedInUser, setLoggedInUser] = useState(Boolean);
  const navigate = useNavigate();

  useEffect(() => {
    // After requesting Github access, Github redirects back to your app with a code parameter
    const url = window.location.href;
    const hasCode = url.includes("?code=");

    // If Github API returns the code parameter
    if (hasCode) {
      const newUrl = url.split("?code=");
      window.history.pushState({}, null, newUrl[0]);
      setData({ ...data, isLoading: true });

      const requestData = {
        code: newUrl[1]
      };


      const proxy_url = "http://localhost:1337/api/authenticate/authenticate";
		async function getGitInfo() {
			const defaultConfig = {
				method: 'POST',
				headers: {
				'Content-Type': 'application/json',
					"Access-Control-Allow-Origin": "*"
				},
			};

		try {
			const endpoint = `${proxy_url}`;
			let res;
			let result;
			res = await (
				await fetch(endpoint, {
					...defaultConfig,
					body: JSON.stringify({
						code: requestData.code,
					})
				})).json();

			console.log(res)
			/* HÄR MÅSTE KOLLA SÅ ATT LOGGIN ÄR OK FRÅN VÅRT API SÄTT SEN ALLA PARAMETRAR TILL ANVÄNDAREN
				FIXA SEN SÅ ATT HAN NAVIGERAS VIDARE TILL LOGINHOME
			*/
			// if (res && res.login) {
			// 	result = await Api.logginUserViaGit(res.login)
			// }
			setLoggedInUser(true)

		} catch (error) {
			console.log((error))
		}

	}
	getGitInfo()
	}
  }, [data]);


	useEffect(() => {
		if(loggedInUser === true) {
			navigate("/login/home")
		}
	}, [loggedInUser, setLoggedInUser])




  return (
    <Wrapper>

        <Loader></Loader>

    </Wrapper>
  );
}
