import styled from "styled-components";



export const Container = styled.div `

	background-color: #8CF3F3;
    height: 100%;
    width: 100%; 
    margin 0 auto;
    display: flex;
    flex-direction: column;
    /* ipads */
    @media (max-width: 700px) {
        flex-direction: column;
		width: 100%;
    }

`;



export const StyleMap = styled.div `{

	border: 20px;
	box-shadow: 10px 8px 20px 5px rgb(0 0 0 / 50%);
	display: flex;
	flex-direction: column;
    min-height: 60%;
    width: 100%; 
	padding: 0.2em;
	background: black;
	}
`;

export const PopUpStyle2 = styled.div `{

    display: flex;
	text-align: center;
	flex-direction: column;
	width: 100%;
    height: 100%

    select {
        margin: 2em 1em 2em 0em;
        padding: 0.7em;
    }
    p{
        padding: 0em;
        margin: 0em;
        display: inline;
        font-size: 1.2em;
        font-weight: bold;
    }

}
`;
