import {useState, useEffect} from 'react';
import Api from '../api';

const useLoadStationsForMap = async (mapRef={}) => {
	const [loadStationObject, setLoadStationObject] = useState("");

    let res;
    useEffect(async () =>  {
        if(!res || res.length === 0){
            let res = await Api.getAllCitys();
            try {
                if (res !== null && mapRef.current.map !== undefined) {
                    var loadStationName;
                    const map = mapRef.current.map;
                    const maps = mapRef.current.maps;

                    res.map(cityCoords => {
                        cityCoords.charging_posts.map(loadStations => {
                            console.log(loadStations)

                            let polyGon = 
                                ([cityCoords.position.polygonePart1,
                                    cityCoords.position.polygonePart2,
                                    cityCoords.position.polygonePart3,
                                    cityCoords.position.polygonePart4
                                ]);
                            loadStationName = new maps.Polygon({
                                paths: polyGon,
                                strokeColor: "yellow",
                                strokeOpacity: 0.9,
                                strokeWeight: 2,
                                fillColor: "transparent",
                                fillOpacity: 0.35,
                                });
                        })
                        loadStationName.addListener('click', (event) => {
                            setLoadStationObject(oldArray => [...oldArray, loadStations]);
                        });
                        loadStationName.setMap(map);
                        setLoadStationObject(oldArray => [...oldArray, loadStationName,]);
                        })
                    }
                    return {loadStationObject};


            } catch (error) {
                console.log(error)
            }

}
}, []);



}
export default useLoadStationsForMap;


































