import {React, useEffect, useState} from "react";

//components
import Loader from "../loader/loader";
import History from "../../routes/History";
import User from "../User";

//styles
import {Container, Main, UserHistory} from './Form.styles'
//other
import Api from "../../api";
const ShowAllCustomers = ()  => {

    const [allCustomers, setAllCustomers] = useState([]);
    const [showCustomerTrips, setShowCustomerTrips] = useState({});

    useEffect(() => {
        async function getFromApi() {
            let res = await Api.getAllUsers();
            setAllCustomers(res.data)
            console.log(res.data)
            console.log(allCustomers.data)

        }


        getFromApi();
    }, [])

    useEffect(() => {

    }, [allCustomers, setAllCustomers])


    function showSpecificCustomer(id) {
        setShowCustomerTrips({
            showCustomer: !showCustomerTrips.showCustomer,
            id: id,
        })
    }





    console.log(allCustomers)
	return (
        <Container>  
            <Main>
                {allCustomers.length > 0 ? (
                <UserHistory>
                    <table>
                    <caption>Alla kunder</caption>
                    <thead>
                        <tr>
                            <th scope="col">Id</th>
                            <th scope="col">Användarnamn</th>
                            <th scope="col">På kontot</th>
                            <th scope="col">Antal Resor</th>
                            <th scope="col">Se alla resor</th>

                        </tr>
                    </thead>
                    <tbody>
                    {allCustomers.map(elem => /* elem.tag !== "admin" && */
                        <tr>
                        <td data-label="Datum"> {elem._id}  </td>
                        <td data-label="Starttid"> {elem.username} </td>
                        <td data-label="Sluttid">{elem.balance} </td>
                        <td data-label="Kostnad">{elem.trips.length} </td>
                        <td data-label="Kostnad">{ <button className="button vertical-align:middle" onClick={(e) => 
                            showSpecificCustomer(elem._id, e)}><span>Visa</span></button>} </td>
                        </tr>
                    )
                    }
                    </tbody>
                    </table>
                </UserHistory>
                ) : (
                    <Loader/>
                )}
            </Main>
        </Container>
    )
}

export default ShowAllCustomers
