import {React, useState} from "react";
//components
import User from "../../components/User/index.js";
//styles
import { Content, Delimiter, StylePayment} from "./Form.styles.js";

const Payment = () => {

    const [inputs, setInputs] = useState({});

    const handleChange = (event) => {
        const name = event.target.name;
        const value = event.target.value;
        //setInputs(values => ({...values, [name]: value}))
    }

    const handleSubmit = async (event)  => {
        // kontrollera inlogg, kolla med servern om användaruppg är ok
        // nu sparar jag lösen som vnaligt lösen, fixa
        event.preventDefault();
    }


	return (
        <Content>
            <p> Minsta möjliga insättning (100:-) </p>
            <StylePayment>
            <form onSubmit={handleSubmit} className = "register">
                <label >
                <input
                    placeholder ="Belopp att sätta in"
                    type="text" 
                    name="username"
                    required
                    value={inputs.username || ""} 
                    onChange={handleChange}
                />
                </label>

                    <Delimiter></Delimiter>
                    <input type="submit" value="Sätt in " />
            </form>
            </StylePayment>
        </Content>
	);
}

export default Payment
