import {React, useState} from "react";

export default function MoveBike(props) {

    const [colorCharg_post, setColorCharg_post] = useState([])
    const [moveBikeTo, setMoveBikeTo] = useState("")


    // Här måste jag hämta på riktigt, nu laddas endast hårdkodade värde in
    async function getLoadStationsForMovingBike() {
        let citys = await Api.getAllCitys();
        citys.forEach(city => { 
            city.charging_posts.map(chargePost => {
                setColorCharg_post(["red", "green", "purple"])
            })
        })
    }

    const handleSubmit = () => {
        if (moveBikeTo != "noValue") {
            console.log("MOVE BIKE ")
        }
    }

	return (
        <div onClick = {getLoadStationsForMovingBike}>
            <select name="bbb" data-testid="dropdown" onChange={(e) => {
            setMoveBikeTo((e.target.value))
            }}>
                <option value={'noValue'}>-- Välj vart cykeln ska förflyttas --</option>
                {colorCharg_post.map(color => color !== null &&
                    <option 
                        key={color}
                        value={color}
                        >
                        {color + " zon"}
                    </option> )}
            </select>
            <div> <button onClick={handleSubmit}> Förflytta cykel </button> </div>
        </div>
	);
}
