import React, { Component, useState } from "react";
import { RequestAuthorizationCode } from "react-oauth2-auth-code-flow";
import ClientOAuth2 from "client-oauth2";
require('dotenv').config()


const oauthClient = new ClientOAuth2({
    clientId: process.env.REACT_APP_GITHUB_CLIENT_ID,
    clientSecret: process.env.REACT_APP_GITHUB_CLIENT_SECRET,
    accessTokenUri: `https://github.com/login/oauth/access_token`, /* måste va rätt */
    authorizationUri: "https://github.com/login/oauth/authorize", /* måste ocksåva rätt */
    redirectUri: "http://localhost:3000/login/home",
    scopes: ["identity"],
});




export default class SendToDropbox extends Component {
    render() {
      return (
        <RequestAuthorizationCode
          oauthClient={oauthClient}
          state={{ from: "/settings" }}
          render={({ url }) => <a href={url}>Connect to Dropbox</a>}
        />
      );
    }
  }






