import React, { useState, useEffect, useContext } from "react";
import { Navigate } from "react-router-dom";
import Styled from "styled-components";
import { AppContext } from "../../AppContext";

require('dotenv').config()


    // clientId: process.env.REACT_APP_GITHUB_CLIENT_ID,
    // clientSecret: process.env.REACT_APP_GITHUB_CLIENT_SECRET,
    // accessTokenUri: `https://github.com/login/oauth/access_token`, /* måste va rätt */
    // authorizationUri: "https://github.com/login/oauth/authorize", /* måste ocksåva rätt */
    // redirectUri: "http://localhost:3000/login/home",
    // scopes: ["identity"],




export default function Login() {
    const { state, dispatch } = useContext(AuthContext);
    const [data, setData] = useState({ errorMessage: "", isLoading: false });

    const { client_id, redirect_uri } = state;

    useEffect(() => {
      // After requesting Github access, Github redirects back to your app with a code parameter
      const url = window.location.href;
      const hasCode = url.includes("?code=");

      // If Github API returns the code parameter
      if (hasCode) {
        const newUrl = url.split("?code=");
        window.history.pushState({}, null, newUrl[0]);
        setData({ ...data, isLoading: true });

        const requestData = {
          code: newUrl[1]
        };

        const proxy_url = state.proxy_url;

        // Use code parameter and other parameters to make POST request to proxy_server
        fetch(proxy_url, {
          method: "POST",
          body: JSON.stringify(requestData)
        })
          .then(response => response.json())
          .then(data => {
            dispatch({
              type: "LOGIN",
              payload: { user: data, isLoggedIn: true }
            });
          })
          .catch(error => {
            setData({
              isLoading: false,
              errorMessage: "Sorry! Login failed"
            });
          });
      }
    }, [state, dispatch, data]);

    if (state.isLoggedIn) {
        Navigate("/home");
    }

    return (
      <Wrapper>
        <section className="container">
          <div>
            <h1>Welcome</h1>
            <span>Super amazing app</span>
            <span>{data.errorMessage}</span>
            <div className="login-container">
              {data.isLoading ? (
                <div className="loader-container">
                  <div className="loader"></div>
                </div>
              ) : (
                <>
                  {
                    // Link to request GitHub access
                  }
                  <a
                    className="login-link"
                    href={`https://github.com/login/oauth/authorize?scope=user&client_id=${process.env.REACT_APP_GITHUB_CLIENT_ID}&redirect_uri=localhost:3000/`}
                    onClick={() => {
                      setData({ ...data, errorMessage: "" });
                    }}
                  >
                    <GithubIcon />
                    <span>Login with GitHub</span>
                  </a>
                </>
              )}
            </div>
          </div>
        </section>
      </Wrapper>
    );
  }
