import React, {useState, useContext } from "react";

//styles
import { Wrapper, Content, SignIn } from './Form.styles.js'
//other
import AppContext from '../../AppContext';



const FormLogin = () => {
    let myContext = useContext(AppContext);
    const [inputs, setInputs] = useState({});

    const handleChange = (event) => {
        const name = event.target.name;
        const value = event.target.value;
        setInputs(values => ({...values, [name]: value}))
    }

      const handleSubmit = async (event)  => {

        // kolla med servern om användaruppg är ok

        localStorage.setItem('user', inputs);
        /* myContext.setUserLoggedIn(inputs);
        myContext.useAuth();
 */
        alert("Välkommen")

        event.preventDefault();
    }

    return (
        <Wrapper>
            <Content>
                <SignIn>
                    <form onSubmit={handleSubmit} className = "register">
                        <label>
                        <input
                            placeholder ="Email"
                            type="text" 
                            name="username"
                            required
                            value={inputs.username || ""} 
                            onChange={handleChange}
                        />
                        </label>
                        <label>
                            <input 
                            placeholder ="Lösenord"

                            type="password"
                            name="psw"
                            required
                            value={inputs.psw || ""} 
                            onChange={handleChange}
                            />
                            </label>
                            <input type="submit" value="Logga in" />
                    </form>
                    <form action="" >
                    <input type="submit" value="Gå till registrera" />
                    </form>
                </SignIn>

            </Content>
        </Wrapper>
    )
}

export default FormLogin
