import React, {useState, useContext } from "react";
import { useNavigate } from "react-router-dom";
//styles
import { Wrapper, Content, SignIn, Delimiter, ScooterImg } from './Form.styles.js'
import scooterPic from '../../images/scooter.png'
//other
import AppContext from '../../AppContext';
import Api from '../../api'
require('dotenv').config()



const FormLogin = () => {


    let myContext = useContext(AppContext);
    const [inputs, setInputs] = useState({});
    const navigate = useNavigate();

    const handleChange = (event) => {
        const name = event.target.name;
        const value = event.target.value;
        setInputs(values => ({...values, [name]: value}))
    }

    const handleSubmit = async (event)  => {
        event.preventDefault();
        localStorage.clear();

        let result = await Api.logginUserViaGit(inputs.username)
        if (result && result.data.type === "success") {
            localStorage.setItem('user', JSON.stringify(inputs.username));
            localStorage.setItem('tag', JSON.stringify(result.data.user.tag));
            myContext.toggleAuth(true);
        } else {
            myContext.toggleAuth(false);
        }

        localStorage.setItem('user', JSON.stringify(inputs));
        myContext.toggleAuth(true);
        navigate("/login/home")
    }

    return (
        <Wrapper>
            <Content>
               <ScooterImg>
                    <img src={scooterPic} alt="download google" width="95%" height="95%" />
               </ScooterImg>

                <SignIn>
                    <form onSubmit={handleSubmit} className = "register">
                        <label >
                        <input
                            placeholder ="Email"
                            type="text" 
                            name="username"
                            required
                            value={inputs.username || ""} 
                            onChange={handleChange}
                        />
                        </label>
                        <label>
                            <input 
                            placeholder ="Lösenord"
                            type="password"
                            name="psw"
                            required
                            value={inputs.psw || ""} 
                            onChange={handleChange}
                            />
                            </label>
                            <input type="submit" value="Logga in" />
                            <Delimiter></Delimiter>
                            <input type="submit" value="Skapa konto" />
                    </form>
                </SignIn>
                <SignIn>
                    <form className = "register">
                        <a href={`https://github.com/login/oauth/authorize?scope=user&client_id=${process.env.REACT_APP_GITHUB_CLIENT_ID}&redirect_uri=http://localhost:3000/login/landingpage`}> Github</a>
                    </form>
                </SignIn>

            </Content>
        </Wrapper>
    )
}

export default FormLogin
