import React, {} from 'react';
import ReactDOM from 'react-dom';

import { render } from "react-dom";
import {
	BrowserRouter,
	Routes,
	Route,
	Navigate,
} from "react-router-dom";

import Header from './components/Header';
import FormLogin from './components/FormLogin';
import Expenses from "./routes/expenses";
import Invoices from "./routes/invoices";
import Invoice from "./routes/invoice";
import Loader from './components/loader/loader';
import AppContext from './AppContext';

//logga in => spara användaruppgifter i state, state sätts av localStorage ? 



function App() {






function useAuth() {
/* 	const [authorized, setAuthorized] = useState("");
	const [tokenWhenLoggedIn, setTokenWhenLoggedIn] = useState("");
	const [user, setUser] = useState(""); */
    const loggedInUser = localStorage.getItem("user");
	console.log(JSON.parse(loggedInUser), "häääääääääär")
	if (loggedInUser) {

		//const foundUser = JSON.parse(loggedInUser);
		//setUser(foundUser);
		return true
	}
}

function PrivateRoute({ children }) {
	const auth = useAuth();
	return auth ? children : <Navigate to="/login" />;
}

/* const userSetting = {
	authorized: authorized,
	authorizedOrNot,
	tokenWhenLoggedIn: tokenWhenLoggedIn,
	setTokenForUser,
	user: user,
	setUserLoggedIn
}; */

const userSetting = {
	"Hej": "hej"	
}



return(
	<>

		<React.StrictMode>
		<BrowserRouter> {/* browserROute runt appen */}

			<Routes>
				<AppContext.Provider value={userSetting}>

				<Route path="/" element={<App />}>
				<Route path="/login" element={<FormLogin />} />

				<Route path="expenses" element={<PrivateRoute> <Expenses /> </PrivateRoute>} />
				<Route path="invoices" element={<PrivateRoute> <Invoices /> </PrivateRoute>}>
				<Route index element={
						<main style={{ padding: "1rem" }}>
							<p>Select an invoice</p>
						</main>
						}
				/>					
				<Route path=":invoiceId" element={ <PrivateRoute> <Invoice/> </PrivateRoute> } /></Route>
					{/* below matching when no other routes will */}
				<Route
					path="*"
					element={
						<main style={{ padding: "1rem" }}>
							{<Loader/>}
						</main>
					}
				/>
				</Route>
				</AppContext.Provider>

			</Routes>
		</BrowserRouter>
		</React.StrictMode>

	</>
)
}

export default App;
