import {React, useState, useContext} from "react";
//components
import User from "../../components/User/index.js";
import Api from "../../api.js";
//styles
import { Content, Delimiter, StylePayment, StylePayment2} from "./Form.styles.js";

const Payment = () => {
    const myContext = useContext(AppContext);
    const [inputs, setInputs] = useState({});
    const [msgForUser, setMsgForUser] = useState("");
    let userName = myContext.user.username;

    const handleChange = (event) => {
        const name = event.target.name;
        const value = event.target.value;
        setInputs(values => ({...values, [name]: value}))
    }

    const handleOnePayment = async (event)  => {

        if (inputs && inputs.onepayment > 0) {
            const res = await Api.updateUserFunds(inputs.onepayment)
            setInputs(null)
            if (res == "OK") {
                setMsgForUser("Din insättning har gått igenom och dina pengar bör nu synas på ditt konto ")
                return
            } 
            setMsgForUser("Något gick fel.. testa igen ")

        }
        event.preventDefault();
    }

    // KOLLA vad meddelandena blir här från backend
    const handleSubscription = async (event)  => {

        if (inputs && inputs.subscription > 0) {
            const res = await Api.updateUserSubsc(inputs.subscription)
            setInputs(null)
            if (res == "OK") {
                setMsgForUser("Din prenumeration är nu godkänd")
                return
            }
            setMsgForUser("Något gick fel.. testa igen ")
        }
        event.preventDefault();
    }



	return (
        <Content>
            <p> Minsta möjliga insättning (100:-) </p>
            <StylePayment>
            <form onSubmit={handleOnePayment} className = "register">
                <label >
                <input
                    placeholder="Belopp att sätta in"
                    type="number" 
                    name="onepayment"
                    required
                    value={inputs.onepayment || ""} 
                    onChange={handleChange}
                />
                </label>
                <Delimiter></Delimiter>
                <input type="submit" value="Sätt in" />
            </form>
            </StylePayment>
            <StylePayment2>
            <p> Prenumerationen kan avslutas närsomhelst  </p>
            <form onSubmit={handleSubscription} className = "register">
                <label >
                <input
                    placeholder="Belopp per månad"
                    type="number" 
                    name="subscription"
                    required
                    value={inputs.subscription || ""} 
                    onChange={handleChange}
                />
                </label>
                <Delimiter></Delimiter>
                <input type="submit" value="Prenumerera" />
            </form>
            </StylePayment2>

        </Content>
	);
}

export default Payment
