import {React, useEffect, useState, useRef } from "react";
//googlemap
import GoogleMapReact from "google-map-react";
//components
import useCurrentLocation from "../../Hooks/currentPosition";
//styles
import {Style} from './Form.styles'


import useSwr from "swr";
const options = {
	enableHighAccuracy: true,
	timeout: 1000 * 60 * 1, // 1 min
	maximumAge: 1000 * 60 * 60, // 1 hour
  };



// style annorlunda jmf med komponenter. Se form.styles,  
export default function MapContainer() {
	const { location, err } = useCurrentLocation(options);
	console.log(location)

	const mapRef = useRef();
	const [zoom, setZoom] = useState(10);
	const [bounds, setBounds] = useState(null);
	const url = "https://data.police.uk/api/crimes-at-location?date=2020-02&lat=52.629729&lng=-1.131592"

	//load data
	/* som att säga: 
	 const fetcher = (...args) => fetch(...args).then(response => response.json());
	*/
	const fetcher = (...args) => fetch(...args).then(response => response.json()); //gör om denna sen

	const {data, error} = useSwr(url, fetcher) //gör om denna sen
	const crimes = data && !error ? data.slice(0,200) : [];
	const Marker = ({children}) => children;

	return (
		<div style={{height: "60vh", width: "60%"}}>
			{location ? (

			<GoogleMapReact 
				bootstrapURLKeys={{key: process.env.REACT_APP_GOOGLE_MAPS_API_KEY}}
				defaultCenter={{ lat: location.latitude, lng: location.longitude}}
				defaultZoom={12}

			>
				{/* markers */}
				{crimes.map(crime => (
					<Marker key={crime.id} lat={crime.location.latitude} lng={crime.location.longitude}>
						<button className="crime-marker">
							<img src="../../images/scooter.png" alt="scooter"></img>
						</button>

					</Marker>

				))}

			</GoogleMapReact>

			):(<p>Loading...</p> )}
		</div>


	);
}
