import {React, useState, useContext, useEffect} from "react";
//components
import User from "../../components/User/index.js";
import Api from "../../api.js";
import AppContext from "../../AppContext.js";
//styles
import { Content, Delimiter, StylePayment, StylePayment2} from "./Form.styles.js";

const initialValue = {
    showMsgSubs: false,
    showMsgOnePay: false,
    msg: ""
}

const Payment = () => {
    const myContext = useContext(AppContext);
    const [inputs, setInputs] = useState({});
    const [msgForUser, setMsgForUser] = useState(initialValue);
    let userName = myContext.user.username;

    const handleChange = (event) => {
        const name = event.target.name;
        const value = parseInt(event.target.value);
        setInputs(values => ({...values, [name]: value}))
        console.log(inputs)

    }


    // KOLLA vad meddelandena blir från backend
    const handleSubmit = async (event)  => {
        console.log(inputs.onepayment)

        let showMsgSubs = false;
        let showMsgOnePay = false;
        let msg = "";
        let res = false;
        let result;
        if (inputs && inputs.subscription > 0) {
            showMsgSubs = true;
            result = await Api.updateUserSubsc(inputs.subscription, userName);
            msg = "Din prenumeration är nu godkänd"

        } else if (inputs && inputs.onepayment > 0) {
            showMsgOnePay = true;
            result = await Api.updateUserFunds(inputs.onepayment, userName);
            msg = "Din insättning har gått igenom";
        } 
        if (result == "EJ OK") {
            msg = "Något gick fel... testa igen senare"
        }
        setMsgForUser({ 
            showMsgSubs: showMsgSubs,
            showMsgOnePay: showMsgOnePay,
            msg: msg,
        })

        // event.preventDefault();

        event.preventDefault();

    }

    // useEffect(() => {

    // }, [setMsgForUser, msgForUser])

	return (
        <Content>
            <StylePayment>
            {msgForUser.showMsgOnePay ? <p>{msgForUser.msg}</p> : (<p> Minsta möjliga insättning (100:-) </p>)}
            <form onSubmit={handleSubmit} className = "register">
                <label >
                <input
                    placeholder="Belopp att sätta in"
                    type="number" 
                    name="onepayment"
                    required
                    value={inputs.onepayment || ""} 
                    onChange={handleChange}
                />
                </label>
                <Delimiter></Delimiter>
                <input type="submit" value="Sätt in" />
            </form>
            </StylePayment>
            <StylePayment2>
            {msgForUser.showMsgSubs ? <p>{msgForUser.msg}</p> : (<p> Prenumerationen kan avslutas närsomhelst  </p>)}
            <form onSubmit={handleSubmit} className = "register">
                <label >
                <input
                    placeholder="Belopp per månad"
                    type="number" 
                    name="subscription"
                    required
                    value={inputs.subscription || ""} 
                    onChange={handleChange}
                />
                </label>
                <Delimiter></Delimiter>
                <input type="submit" value="Prenumerera" />
            </form>
            </StylePayment2>
        </Content>
	);
}

export default Payment
