import {React, useState, useEffect} from 'react';
import { Routes, Route, Outlet, Link, Navigate } from "react-router-dom";

import FormLogin from './components/FormLogin';
import Expenses from "./routes/expenses";
import Invoices from "./routes/invoices";
import Invoice from "./routes/invoice";
import Loader from './components/loader/loader';
import AppContext from './AppContext';
import Header from './components/Header';
import Footer from './components/Footer'
import Welcome from './components/welcome/welcome';
//logga in => spara användaruppgifter i state, state sätts av localStorage ? 

/* 

function useAuth() {
	const loggedInUser = localStorage.getItem("user");

	if (loggedInUser) {
		//const foundUser = JSON.parse(loggedInUser);
		//setUser(foundUser);
		return true
	} else {
		return false
	}
} 
*/
/*  	function PrivateRoute({ children }) {
		const auth = useAuth();
		return auth ? children : <Navigate to="/login" />;
	}   */



export default function App() {


/* 	const [user, setUser] = useState();
 */

	const [user, setUser] = useState({});
	const [auth, setAuth] = useState({});

	useEffect(() => {
		const loggedInUser = localStorage.getItem("user");
		if (loggedInUser) {
		  const foundUser = JSON.parse(loggedInUser);
		  setUser(foundUser);
		  setAuth(true)
		}
	  }, []);

	  function PrivateRoute({ children }) {
		const authorized = auth;
		return authorized ? children : <Navigate to="/login" />;
	}  



    const setAutorized = (value) => {
		setAuth(value)
    };


	const userInfo = {
		setUserLoggedIn,
		user: user,
		useAuth
	}



	{/* routes och nesting routes, dubbelkolla detta */}

	return(
	<>
	<AppContext.Provider value={userInfo}>
	<Header/>
	<Routes>
		<Route path="/" element={ <Welcome/>} />
			<Route path="login" element={ <FormLogin />} />
			<Route path="expenses" element={<PrivateRoute> <Expenses /> </PrivateRoute>} />
			<Route path="invoices" element={<PrivateRoute> <Invoices />  </PrivateRoute>} >
				<Route index element={
						<main style={{ padding: "1rem" }}>
							<p>Select an invoice</p>
						</main>
						}
				/>			
			</Route>
		<Route path=":invoiceId" element={ <PrivateRoute> <Invoice/> </PrivateRoute> } /> 
		<Route path="*" element={ <Loader/>}/>
	</Routes>
	<Footer/>
	</AppContext.Provider>

	</>
	)
}
