import React, { useState, useEffect, useContext } from "react";
import { Navigate } from "react-router-dom";
import {Wrapper} from "./Form.styles";
import { AppContext } from "../../AppContext";
import Api from "../../api";

require('dotenv').config()


    // clientId: process.env.REACT_APP_GITHUB_CLIENT_ID,
    // clientSecret: process.env.REACT_APP_GITHUB_CLIENT_SECRET,
    // accessTokenUri: `https://github.com/login/oauth/access_token`, /* måste va rätt */
    // authorizationUri: "https://github.com/login/oauth/authorize", /* måste ocksåva rätt */
    // redirectUri: "http://localhost:3000/login/home",
    // scopes: ["identity"],




export default function Logins() {
    const [data, setData] = useState({ errorMessage: "", isLoading: false });


    useEffect(() => {
      // After requesting Github access, Github redirects back to your app with a code parameter
        const url = window.location.href;
        const hasCode = url.includes("?code=");
        console.log(hasCode);

      // If Github API returns the code parameter
      if (hasCode) {
        const newUrl = url.split("?code=");
        console.log(("HÄÄÄÄÄÄÄÄR, ", hasCode))
        window.history.pushState({}, null, newUrl[0]);
        setData({ ...data, isLoading: true });


        const requestData = {
            client_id: process.env.REACT_APP_GITHUB_CLIENT_ID,
            client_secret: process.env.REACT_APP_GITHUB_CLIENT_SECRET,
            code: newUrl[1],
            redirect_uri: "http://localhost:3000/loggin/oauth"
        };

        console.log(requestData)

        const postUrl = "https://github.com/login/oauth/access_token";

        async function hej(){

                const endpoint = `${postUrl}`
                const document = await (
                    await fetch(endpoint, {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                        "Access-Control-Allow-Origin": "*"
                    },
                    body: JSON.stringify( {docname: docname, text: text} )
                    })
                ).json();

        }


        fetch(postUrl, {
            method: "POST",
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(requestData)
            })
            .then(response => response.json())
            .then(data => {
                console.log(data)
            })
            .catch(error => {
                setData({
                isLoading: false,
                errorMessage: "Sorry! Login failed"
                });
            }); 

        }
      }, [data]);




    return (
      <Wrapper>
        <>
            {
            // Link to request GitHub access
            }

            <a
            className="login-link"
            onClick={() => {
                setData({ ...data, errorMessage: "" });
            }}
                href={`https://github.com/login/oauth/authorize?scope=identity&client_id=${process.env.REACT_APP_GITHUB_CLIENT_ID}`}

            >
            <span>Login with GitHub</span>
            </a>
        </>

      </Wrapper>
    );
  }
