export default handleApiLoaded = (mapRef, props) => {
	if (props.loadingCoordinates !== null && mapRef.map !== null) {
		const map = mapRef.map;
		const maps = mapRef.maps;

		console.log(mapRef)

		props.loadingCoordinates.map(coords => 
			{
			const polyGon = 
				([coords.position.polygonePart1,
					coords.position.polygonePart2,
					coords.position.polygonePart3,
					coords.position.polygonePart4
				]);

				var bermudaTriangle = new maps.Polygon({
				paths: polyGon,
				strokeColor: "#FF0000",
				strokeOpacity: 0.8,
				strokeWeight: 2,
				fillColor: "#FF0000",
				fillOpacity: 0.35,
				text: "HEJ"
				});

			bermudaTriangle.setMap(map);
		})

	}
}





