import {React, useEffect, useState, useRef } from "react";
//googlemap
import GoogleMapReact from "google-map-react";
//components
import useCurrentLocation from "../../Hooks/currentPosition";
import PopUp from "./popup";
import LoadCitys from "./helperFunctions/loadCitys"
//styles & img
import {Style} from './Form.styles'
import bikeimg from '../../images/bike.png'
import personimg from '../../images/person.png'


// options till userLocation
const options = {
	enableHighAccuracy: true,
	timeout: 1000 * 60 * 1, // 1 min
	maximumAge: 1000 * 60 * 60, // 1 hour
};

export default function MapContainer(props) {
	const { location, err } = useCurrentLocation(options);
	const mapRef = useRef();
	const [zoom, setZoom] = useState(10);
	const [bounds, setBounds] = useState(null);
	const showBikesOrNot = props.bikes.getbike;
	var bikeArray = props.bikes.content;
	bikeArray =  bikeArray.slice(0, 200); // innan jag fixar med cluster max 200

	const [popupInfo , setPopupInfo] = useState("");
	const Marker = ({ children }) => <div>{children}</div>;
	console.log(props)

	if (props.loadingCoordinates !== null && mapRef.current !== null) {

		console.log(mapRef.current)

			var cityname;
			// const map = mapRef.current.map;
			// const maps = mapRef.current.maps;

			// props.loadingCoordinates.content.map(cityCoords =>
			// 	{
			// 	cityname = cityCoords.city
			// 	let polyGon = 
			// 		([cityCoords.position.polygonePart1,
			// 			cityCoords.position.polygonePart2,
			// 			cityCoords.position.polygonePart3,
			// 			cityCoords.position.polygonePart4
			// 		]);

			// 	if (props.getStations === true) {
			// 		cityname = new maps.Polygon({
			// 			paths: polyGon,
			// 			strokeColor: "#FF0000",
			// 			strokeOpacity: 0.9,
			// 			strokeWeight: 2,
			// 			fillColor: "transparent",
			// 			fillOpacity: 0.35,
			// 			});
			// 		cityname.setMap(map);
			// 		console.log("Setmap")
			// 		cityname.onClick={handleToggle() {
			// 			console.log("HEasdasaddsaJ")
			// 		}}

			// 		}
			// 	})
		}


		// LoadCitys(mapRef.current, props.loadingCoordinates);













	return (
		<Style>
			{/* 		KARTA		 */}
			{location ? (
			<GoogleMapReact 
				bootstrapURLKeys={{key: process.env.REACT_APP_GOOGLE_MAPS_API_KEY}}
				defaultCenter={{ lat: location.latitude, lng: location.longitude}}
				defaultZoom={3}
				yesIWantToUseGoogleMapApiInternals
				onGoogleApiLoaded= {({map, maps}) => {
					mapRef.current = {map, maps};
					// LoadCitys(mapRef.current); 

				}}
				onChange={({zoom, bounds, map, maps}) => {
					setZoom(zoom)
					setBounds([
						bounds.nw.lng,
						bounds.se.lat,
						bounds.se.lng,
						bounds.nw.lat
					])
				}}
			>
			{/* 		1 Person 		*/}
			<Marker 
				key={1} 
				lat={location.latitude} 
				lng={location.longitude}
				>
				<div>
					<img className = "crime-marker" src={personimg} alt="scooter" />
				</div>	
			</Marker>
			{/* 		Alla cyklar		 */}
			{showBikesOrNot ? (
			bikeArray.map(bike => {
			return (
			<Marker 
				key={bike.id} 
				lat={bike.location.latitude} 
				lng={bike.location.longitude}
				>
				<div onClick={() => setPopupInfo(bike)}className="crime-marker">
				<img className = "scooter" src={bikeimg} alt="scooter"/>
				</div>
			</Marker>
			)
			})
			) : (console.log("HE"))}
			</GoogleMapReact>
			):(<p>Loading...</p> )}
			{/*				 POPUP				 */}
			{popupInfo ? (
			<PopUp
				key={"popupkey"}
				store={popupInfo} 
				>
			</PopUp>
			): (console.log("EJ POPUP"))}
		</Style>
	);
}
