import {React, useEffect, useState} from "react";

//components
//styles
import {Container, Main, UserHistory} from './Form.styles'
//other
import Api from "../../api";

const ShowAllCustomers = ()  => {

    const [allCustomers, setAllCustomers] = useState([]);

    useEffect(() => {
        async function getFromApi() {
            let res = await Api.getAllUsers();
            setAllCustomers(res)
            console.log(res)
            console.log(allCustomers)

        }


        getFromApi()
    }, [])



	return (
        <Container>  
            <Main>

            <UserHistory> 
                        <table>
                        <caption>Alla kunder</caption>
                        <thead>
                            <tr>
                                <th scope="col">Id</th>
                                <th scope="col">Användarnamn</th>
                                <th scope="col">På kontot</th>
                                <th scope="col">Resor</th>
                                <th scope="col">Kostnad</th>

                            </tr>
                        </thead>
                        <tbody>
                        {allCustomers.map(elem => 
                            <tr>
                            <td data-label="Datum"> {elem.date}  </td>
                            <td data-label="Starttid"> {elem.start.time} </td>
                            <td data-label="Sluttid">{elem.stop.time} </td>
                            <td data-label="Kostnad">{elem.price}:- </td>
                            </tr>
                        )
                        }
                        </tbody>
                        </table>
                    </UserHistory>

            </Main>
        </Container>
    )
}

export default ShowAllCustomers
