import {useState, useEffect} from 'react';
import Api from '../api';

const useLoadStationsForMap = async (mapRef={}) => {

	const [LoadStationObject, setLoadStationObject] = useState("");
    const [cityFromApi, setCityFromApi] = useState(0);
    let res;

    useEffect( async () =>  {
        if(!res || res.length === 0){
            let res = await Api.getAllCitys();
            console.log("HEJsadasdddddddddddddddddd")
            setCityFromApi(res)
        }
    }, [mapRef]);

    try {

        if (cityFromApi !== null && mapRef.current.map !== undefined) {

            console.log(mapRef.current.map)
            console.log(mapRef.current.maps)

            var cityname;
            const map = mapRef.current.map;
            const maps = mapRef.current.maps;
            res.map(cityCoords => {

                cityCoords.charging_posts.map(loadStations => {
                    console.log(loadStations)

                    let polyGon = 
                        ([cityCoords.position.polygonePart1,
                            cityCoords.position.polygonePart2,
                            cityCoords.position.polygonePart3,
                            cityCoords.position.polygonePart4
                        ]);
                    cityname = new maps.Polygon({
                        paths: polyGon,
                        strokeColor: "yellow",
                        strokeOpacity: 0.9,
                        strokeWeight: 2,
                        fillColor: "transparent",
                        fillOpacity: 0.35,
                        });
                })
                // cityname.addListener('click', (event) => {
                //     console.log("HEJ", cityCoords)
                //     setPopupInfo({	
                //         showBike: false,
                //         showCity: true,
                //         showLocation: false,
                //         content: cityCoords
                //     })
                // });
                cityname.setMap(map);
                setLoadStationObject(oldArray => [...oldArray, cityname]);

                })
            }
            return {LoadStationObject};


    } catch (error) {
        console.log(error)
    }



}
export default useLoadStationsForMap;


































