import styled from "styled-components";

export const PopUpStyle = styled.div `

background-color: #1f2a32;
color:#F1F1F1;
padding: 2em;
margin-top: 1em;
margin-bottom: 1em;
border: 2px solid black;
display: flex;
text-align: left;
flex-direction: column;
width: 100%;
min-height: 15em;


select {
    margin: 2em 1em 2em 0em;
    padding: 0.7em;
}
p{
    padding: 0em;
    margin: 0em;
    display: inline;
    font-size: 1.2em;
    font-weight: bold;
}

@media (max-width: 700px) {
    flex-direction: column;
    width: 100%;
    padding: 0em;
}
}
`;
