import Api from '../api';
import {useState, useEffect} from 'react';

const options = {
	enableHighAccuracy: true,
	timeout: 1000 * 60 * 1, // 1 min
	maximumAge: 1000 * 60 * 60, // 1 hour
};

const useCurrentPosition2 = (mapRef) => {
    console.log(mapRef)
    const[location1, setLocation1] = useState("");
    const [error1, setError1] = useState();
    const handleSucces = (pos) => {
        // const {latitude, longitude } = pos.coords;

        setLocation1({
            mapRef
        });
    };

    const handleError = (error) => {
        setError1(error.message);
    };

    useEffect(async () => {
        const {geolocation} = navigator;
        let res;
        res = await Api.getAllCitys();
        if (res != null) {
            handleSucces(res);
        }
        else {
            setError1("Geolocation not supported")
            return;
        }
        geolocation.getCurrentPosition(handleSucces, handleError, options)
    },[options])

    return {location1, error1};
};
export default useCurrentPosition2;






