import {React, useEffect, useState, useRef } from "react";
//googlemap
import GoogleMapReact from "google-map-react";
//components
import useCurrentLocation from "../../Hooks/currentPosition";
import useSuperCluster from 'use-supercluster';
//styles
import {Style, BtnStyle} from './Form.styles'
import scooter from '../../images/bike.png'
import useSwr from "swr";
const options = {
	enableHighAccuracy: true,
	timeout: 1000 * 60 * 1, // 1 min
	maximumAge: 1000 * 60 * 60, // 1 hour
  };



// style annorlunda jmf med komponenter. Se form.styles,  
export default function MapContainer() {
	const { location, err } = useCurrentLocation(options);
	const mapRef = useRef();
	const [zoom, setZoom] = useState(10);
	const [bounds, setBounds] = useState(null);
	const [forMarker, setForMarker] = useState(null);

	useEffect(() => {
		setForMarker()
		return () => {
			cleanup
		}
	}, [input])


	const Marker = ({children}) => children;
	//clusters
	return (
		<Style>
			{location ? (

			<GoogleMapReact 
				bootstrapURLKeys={{key: process.env.REACT_APP_GOOGLE_MAPS_API_KEY}}
				defaultCenter={{ lat: location.latitude, lng: location.longitude}}
				defaultZoom={4}
				yesIWantToUseGoogleMapApiInternals
				onGoogleApiLoaded= {({map}) => {
					mapRef.current = map;
				}}
				onChange={({zoom, bounds}) => {
					setZoom(zoom)
					setBounds([
						bounds.nw.lng,
						bounds.se.lat,
						bounds.se.lng,
						bounds.nw.lat
					])
				}}

			>
			{crimes.map(crime => {

				return (
				<Marker 
					key={crime.id} 
					lat={crime.location.latitude} 
					lng={crime.location.longitude}>

					<BtnStyle>
						<button onClick= {() => {
								<div>HEJ</div>
							}}
							className="crime-marker">
							<img className = "scooter" src={scooter} alt="scooter"/>
						</button>
					</BtnStyle>
				</Marker>
				)
			})}
			</GoogleMapReact>
			):(<p>Loading...</p> )}
		</Style>	

	);
}
