import React, {useState } from "react";

//styles
import { Wrapper, Content, Signup } from './Form.styles.js'

//other
/* import apisetting from "../../API.js";
import AppContext from '../authorized';
 */

const FormLogin = () => {
    const [inputs, setInputs] = useState({});

    const handleChange = (event) => {
        const name = event.target.name;
        const value = event.target.value;
        setInputs(values => ({...values, [name]: value}))
    }

      const handleSubmit = async (event)  => {

        // send the username and password to the server
        //setUser(response.data) ?
        // använder localStorage, rätt fel? Spara i state? Alternativ?
        localStorage.setItem('user', inputs)
        alert("Välkommen")

        event.preventDefault();

    }

    return (
        <Wrapper>
            <Content>
                <h2> Logga in </h2>
                <Signup>
                    <form onSubmit={handleSubmit} className = "register">
                        <label>Enter email:
                        <input 
                            type="text" 
                            name="username"
                            required
                            value={inputs.username || ""} 
                            onChange={handleChange}
                        />
                        </label>
                        <label>Enter password:
                            <input 
                            type="password"
                            name="psw"
                            required
                            value={inputs.psw || ""} 
                            onChange={handleChange}
                            />
                            </label>
                            <input type="submit" value="Logga in" />
                    </form>
                </Signup>
                <form action="" >
                <input type="submit" value="Gå till registrera" />
                </form>
            </Content>
        </Wrapper>
        )        
}


export default FormLogin
