import {React, useEffect, useState, useRef } from "react";
//googlemap
import GoogleMapReact from "google-map-react";
//components
import useCurrentLocation from "../../Hooks/currentPosition";
import PopUp from "./popup";
import LoadCitys from "./helperFunctions/loadCitys"
import Api from "../../api";
//styles & img
import {Style} from './Form.styles'
import bikeimg from '../../images/bike.png'
import personimg from '../../images/person.png'


// options till userLocation
const options = {
	enableHighAccuracy: true,
	timeout: 1000 * 60 * 1, // 1 min
	maximumAge: 1000 * 60 * 60, // 1 hour
};


let initalValuePopUp = {
	showBike: false,
	showCity: false,
	showLocation: false,
	content: []
};

export default function MapContainer(props) {
	const { location, err } = useCurrentLocation(options);
	const mapRef = useRef();
	const [citysObject, setCitysObject] = useState([]);
	// const showBikesOrNot = props.scooter.showScooters;
	// var bikeArray = props.scooter.content;
	// bikeArray =  bikeArray.slice(0, 200); // innan jag fixar med cluster max 200
	const [popupInfo , setPopupInfo] = useState(initalValuePopUp);
	const Marker = ({ children }) => <div>{children}</div>;

	console.log(props.scooter)
	useEffect(() => {
		if (props.ifToShowCity.loadCity === true) {
			loadCitysToMap(mapRef.current) 
		} else {
			removeCitysFromMap(mapRef.current)
		}
	}, [props.ifToShowCity.loadCity])

	function removeCitysFromMap() {
		if (citysObject !== undefined) {
			citysObject.map(city => {
				city.setMap(null)
			})
		}
	}

	async function loadCitysToMap(mapRef) {
		try {
            let res = await Api.getAllCitys();
			if (res !== null && mapRef.current !== null) {
				console.log(props)

				var cityname;
				const map = mapRef.map;
				const maps = mapRef.maps;

				res.map(cityCoords => {
					console.log(cityCoords)

					cityname = cityCoords.city
					let polyGon = 
						([cityCoords.position.polygonePart1,
							cityCoords.position.polygonePart2,
							cityCoords.position.polygonePart3,
							cityCoords.position.polygonePart4
						]);
					cityname = new maps.Polygon({
						paths: polyGon,
						strokeColor: "#FF0000",
						strokeOpacity: 0.9,
						strokeWeight: 2,
						fillColor: "transparent",
						fillOpacity: 0.35,
						});

					cityname.addListener('click', (event) => {
						console.log("HEJ", cityCoords)
						setPopupInfo({	
							showBike: false,
							showCity: true,
							showLocation: false,
							content: cityCoords
						})
					});
					cityname.setMap(map);


					setCitysObject(oldArray => [...oldArray, cityname]);
					})
				}

        } catch (error) {
			console.log(error)
        }
	}

	return (
		<Style>
			{/* 		KARTA		 */}
			{location ? (
			<GoogleMapReact 
				bootstrapURLKeys={{key: process.env.REACT_APP_GOOGLE_MAPS_API_KEY}}
				defaultCenter={{ lat: location.latitude, lng: location.longitude}}
				defaultZoom={10}
				yesIWantToUseGoogleMapApiInternals
				onGoogleApiLoaded= {({map, maps}) => {
					mapRef.current = {map, maps};
				}}
			>
			{/* 		1 Person 		*/}
			<Marker 
				key={1} 
				lat={location.latitude} 
				lng={location.longitude}
				>
				<div >
					<img className = "crime-marker" src={personimg} alt="person" />
				</div>	
			</Marker>
			{/* 		Alla cyklar		 */}
			{/* {showBikesOrNot ? (
			bikeArray.map(bike => {
			return (
			<Marker 
				key={bike.id} 
				lat={bike.location.latitude} 
				lng={bike.location.longitude}
				>
				<div onClick={() => setPopupInfo({
					showBike: true,
					showCity: false,
					showLocation: false,
					content: bike
					})}className="crime-marker">
				<img className = "scooter" src={bikeimg} alt="scooter"/>
				</div>
			</Marker>)
			})
			) : (console.log("HE"))} */}


			</GoogleMapReact>
			):(<p>Loading...</p> )}

			{/*				 POPUP				 */}
			{popupInfo ? (
			<PopUp
				key={"popupkey"}
				store={popupInfo} 
				>
			</PopUp>
			): (console.log("EJ POPUP"))}
		</Style>
	);
}
