import {React, useState } from "react";
//styles
import {PopUpStyle} from './Form.styles'
import Api from "../../api";

export default function PopUp(props) {
    let content;
    let whatToShow;
    const [colorCharg_post, setColorCharg_post] = useState([])
    const [moveBikeTo, setMoveBikeTo] = useState("")


    if (props.PopupInfo && props.PopupInfo.content ) {
        content = props.PopupInfo.content
        whatToShow = props.PopupInfo.whatToShow
    }


    // Här måste jag hämta på riktigt, nu laddas endast hårdkodade värde in
    async function getLoadStationsForMovingBike() {
        let citys = await Api.getAllCitys();
        citys.forEach(city => { 
            city.charging_posts.map(chargePost => {
                setColorCharg_post(["red", "green", "purple"])
            })
        })
    }


    const handleSubmit = () => {



        console.log("HEJ", moveBikeTo)
    }


	return (
		<PopUpStyle >
        { whatToShow === "Scooter"  ? (
            <>
            <div> Scooter ID: {content._id} Batteri: {content.battery}</div>
            <div> Stad: {content.city_location} Position, Latitude: {content.position.lat} Longitude: {content.position.long} </div>
            <div> Hastighet {content.speed} </div>
            <div> Starttid {content.start_time} </div>

            <div onClick = {getLoadStationsForMovingBike}>
                <select name="bbb" data-testid="dropdown" onChange={(e) => {
                setMoveBikeTo((e.target.value))
                }}>
                    <option value={'emptyvalue'}>-- Välj vart cykeln ska förflyttas --</option>
                    {colorCharg_post.map(color => color !== null &&
                        <option 
                            key={color}
                            value={color}
                            >
                            {color + " zon"}
                        </option> )}
                </select>
                <div> <button onClick={handleSubmit}> Förflytta cykel </button> </div>

            </div>




            </>
            ) : null }

        { whatToShow === "City" ? (
            <>
            <div> Stad {content.city}</div>
            <div> Antal cyklar {content.amount_of_bikes} </div>
            </>
            ) : null } 

        {  whatToShow === "LoadStation" ? (
            <>
            <div> Position (Visar endast 2 av positionerna nu) 
                {content.position.polygonePart1.lat }
                { content.position.polygonePart2.lng }
            </div>
            <div> Antal cyklar ??????</div>
            </>
            ) : null } 
		</PopUpStyle>	
	);
}









