import styled from "styled-components";



export const Container = styled.div `

	background-color: #8CF3F3;
    min-height: 100vh;
    width: 100%; 
    display: flex;
    flex-direction: column;

    /* ipads */
    @media (max-width: 700px) {
        flex-direction: column;
		width: 100%;
    }

`;



export const Main = styled.div `{
	display: flex;
	flex-direction: column;

	@media (max-width: 1000px) {
		flex-direction: column;
		width: 90%;
		height: 90%;
		padding-right:0em;

	}
}
`


export const StyleMap = styled.div `{
	display: flex;
	flex-direction: column;
	border: 20px;
	box-shadow: 10px 8px 20px 5px rgb(0 0 0 / 50%);
    min-height: 100%;
    min-height: 400px;

    width: 100%; 
	padding: 0.2em;
	background: black;
	}
`;

export const PopUpStyle2 = styled.div `{

	text-align: center;
	width: 100%;
    height: 100%

    select {
        margin: 2em 1em 2em 0em;
        padding: 0.7em;
    }
    p{
        padding: 0em;
        margin: 0em;
        display: inline;
        font-size: 1.2em;
        font-weight: bold;
    }

}
`;
