import styled from "styled-components";



export const Container = styled.div `

	background-color: #8CF3F3;
    min-height: 100vh;
    width: 100%; 
    margin 0 auto;
    display: flex;
    flex-direction: column;
    //padding-bottom: 8em;/* Footer height */

    /* ipads */
    @media (max-width: 700px) {
        flex-direction: column;
		width: 100%;
    }

`;



export const Main = styled.div `{
	display: flex;
	flex-direction: column;
	width: 100%;
    height: 100%;

	@media (max-width: 1000px) {
		flex-direction: column;
		width: 90%;
		height: 90%;
		padding-right:0em;

	}
}
`




export const PopUpStyle2 = styled.div `{

	text-align: center;
	width: 100%;
    height: 100%

    select {
        margin: 2em 1em 2em 0em;
        padding: 0.7em;
    }
    p{
        padding: 0em;
        margin: 0em;
        display: inline;
        font-size: 1.2em;
        font-weight: bold;
    }

}
`;
