import {React, useState, useContext, useEffect} from "react";
//components
import User from "../../components/User/index.js";
import Api from "../../api.js";
import AppContext from "../../AppContext.js";
//styles
import { Content, Delimiter, StylePayment, StylePayment2} from "./Form.styles.js";

const initialValue = {
    showMsgSubs: false,
    showMsgOnePay: false,
    msg: ""
}

const Payment = () => {
    const myContext = useContext(AppContext);
    const [inputs, setInputs] = useState({});
    const [msgForUser, setMsgForUser] = useState(initialValue);
    const [subscription, setSubscription] = useState(0);
    const [onePayment, setOnePayment] = useState(0);

    let userName = myContext.user.username;

    const handleChange = (event) => {
        const name = event.target.name;
        const value = parseInt(event.target.value);
        setInputs(values => ({...values, [name]: value}))
        console.log(inputs)

    }


    // KOLLA vad meddelandena blir från backend
    const handleSubmit = async (event)  => {

        let showMsgSubs = false;
        let showMsgOnePay = false;
        let msg = "";
        let result;

        if (subscription !== 0) {
            showMsgSubs = true;
            result = await Api.updateUserSubsc(subscription, userName);
            msg = "Din prenumeration är nu godkänd"

        } else if (onePayment) {
            showMsgOnePay = true;
            if (onePayment > 100) {
                result = await Api.updateUserFunds(onePayment, userName);
                msg = "Din insättning har gått igenom";
            }else {
                msg = "Minsta möjliga insättning är 100:-";
            }
        } 
        if (result !== "OK") {
            msg = "Något gick fel... testa igen senare"
        } 
        setMsgForUser({ 
            showMsgSubs: showMsgSubs,
            showMsgOnePay: showMsgOnePay,
            msg: msg,
        })
        setOnePayment();
        setSubscription();
        event.preventDefault();
    }

    useEffect(() => {

    }, [setMsgForUser, msgForUser])

	return (
        <Content>
            <StylePayment>
            {msgForUser.showMsgOnePay ? <p>{msgForUser.msg}</p> : (<p> Minsta möjliga insättning (100:-) </p>)}
            <form onSubmit={handleSubmit} className = "register">
                <label >
                <input
                    placeholder="Belopp att sätta in"
                    type="number" 
                    name="onepayment"
                    required
                    //value={onepayment || ""} 
                    onChange={(e) => setOnePayment(e.target.value)}
                />
                </label>
                <Delimiter></Delimiter>
                <input type="submit" value="Sätt in" />
            </form>
            </StylePayment>
            <StylePayment2>
            {msgForUser.showMsgSubs ? <p>{msgForUser.msg}</p> : (<p> Prenumerationen kan avslutas närsomhelst  </p>)}
            <form onSubmit={handleSubmit} className = "register">
                <label >
                <input
                    placeholder="Belopp per månad"
                    type="number" 
                    name="subscription"
                    required
                    //value={inputs.subscription || ""} 
                    onChange={(e) => setSubscription(e.target.value)}
                />
                </label>
                <Delimiter></Delimiter>
                <input type="submit" value="Prenumerera" />
            </form>
            </StylePayment2>
        </Content>
	);
}

export default Payment
