import {React, useState } from "react";


export default function handleApiLoaded(mapRef, props) {

	if (props.content.length > 0 && mapRef.map !== null) {
		var area;
		const map = mapRef.map;
		const maps = mapRef.maps;
		const [cityState, setCityState] = useState();

			props.content.map(coords => 
				{
				let polyGon = 
					([coords.position.polygonePart1,
						coords.position.polygonePart2,
						coords.position.polygonePart3,
						coords.position.polygonePart4
					]);

					area = new maps.Polygon({
					paths: polyGon,
					strokeColor: "#FF0000",
					strokeOpacity: 0.8,
					strokeWeight: 2,
					fillColor: "#FF0000",
					fillOpacity: 0.35,
					});
					if (props.getStations === false) {
						for(let i = 0; i<area.length; i++) {
							area.setMap(null);
							cityState[i].setMap(null)
						}
						// måste nog spara i state för att nå det. 
					} else {
						setCityState(area)
						area.setMap(map);


						// infoWindow = new google.maps.InfoWindow();

					}
			})
		}
	return (null)
}




