import {React, useEffect, useState, useRef } from "react";
//googlemap
//components
import useSuperCluster from 'use-supercluster';
//styles
import {BtnStyle} from './Form.styles'
import scooter from '../../images/scooter.png'
import useSwr from "swr";


// style annorlunda jmf med komponenter. Se form.styles,  
export default function Cluster({stateForMarker}) {
	const url = "https://data.police.uk/api/crimes-street/all-crime?lat=52.629729&lng=-1.131592&date=2020-01"

  const bounds = stateForMarker.bounds;
  const zoom = stateForMarker.zoom;
  const mapRef = stateForMarker.mapRef;
	const fetcher = (...args) => fetch(...args).then(response => response.json()); //gör om denna sen
	const Marker = ({children}) => children;
	const {data, error} = useSwr(url, fetcher) //gör om denna sen
	const crimes = data && !error ? data : [];
	//clusters
	const points = crimes.map(crime => ({
		type: "Feature",
		properties: {
			cluster: false,
			crimeId: crime.id,
			category: crime.category
		},
		geometry: {
			type: "Point", 
			coordinates:[
				parseFloat(crime.location.longitude), 
				parseFloat(crime.location.latitude)
			]}
	}))

    const { clusters, supercluster } = useSuperCluster({
		points, 
		bounds,
		zoom,
		options: {radius:75, maxZoom:20}
	});


	return (
        <>
        { stateForMarker ? (
        clusters.map(cluster => {
            const[longitude, latitude] = cluster.geometry.coordinates;
            const {cluster : isCluster, point_count: pointCount} = cluster.properties;
            if (isCluster) {
                return (
                    <Marker key={cluster.id} 
                        lat={latitude} 
                        lng={longitude}>
                            <div 
                                className="cluster-marker"
                                style={{
                                    width: `${10 + (pointCount/points.length)* 20}px`,
                                    height: `${10 + (pointCount/points.length)* 20}px`
                                }}
                                onClick= {() => {
                                    const expansionZoom = Math.min(
                                        supercluster.getClusterExpansionZoom(cluster.id),
                                        30
                                    );
                                    mapRef.current.setZoom(expansionZoom) //zoomar in om man klickar på en cluster
                                    mapRef.current.panTo({lat: latitude, lng: longitude});  //sätter zoom till rätt koordinat
                                    console.log("Här", latitude, longitude)
                                }}
                                >
                            {pointCount}</div>
                    </Marker>
                );
            }
            return (

            <Marker 
                key={cluster.properties.crimeId} 
                lat={latitude} 
                lng={longitude}>

                <BtnStyle>
                    <button onClick= {() => {
                            <div>HEJ</div>
                        }}
                        className="crime-marker">
                        <img className = "scooter" src={scooter} alt="scooter"/>
                    </button>
                </BtnStyle>
            </Marker>
            )
        })
        ) : console.log("HEJ")}
        </>		
	);
}
