import React, {useState, useContext } from "react";
import { useNavigate, Redirect } from "react-router-dom";

//styles
import { Wrapper, Content, SignIn, Delimiter, ScooterImg } from './Form.styles.js'
import scooterPic from '../../images/scooter.png'

//other
import AppContext from '../../AppContext';
import Api from "../../api.js";



const FormLogin = () => {
    let myContext = useContext(AppContext);
    const [inputs, setInputs] = useState({});
    const navigate = useNavigate();

    const handleChange = (event) => {
        const name = event.target.name;
        const value = event.target.value;
        setInputs(values => ({...values, [name]: value}))
    }

    const handleSubmit = async (event)  => {
        // kontrollera inlogg, kolla med servern om användaruppg är ok
        // nu sparar jag lösen som vnaligt lösen, fixa
        localStorage.setItem('user', JSON.stringify(inputs));
        myContext.toggleAuth(true);
        navigate("/home")
        event.preventDefault();
    }

    const handleSubmit2 = async (event)  => {
        // kontrollera inlogg, kolla med servern om användaruppg är ok
        // nu sparar jag lösen som vnaligt lösen, fixa

        Redirect("/https://github.com/login/oauth/authorize?client_id=7c488a6f93e063d494fb")
        navigate()

        //let res = await Api.getLoginOauth();
        //console.log(res)
        // localStorage.setItem('user', JSON.stringify(inputs));
        // myContext.toggleAuth(true);
        // navigate("/home")
        event.preventDefault();
    }

    return (
        <Wrapper>
            <Content>
               <ScooterImg>
                    <img src={scooterPic} alt="download google" width="95%" height="95%" />
               </ScooterImg>

                <SignIn>
                    <form onSubmit={handleSubmit} className = "register">
                        <label >
                        <input
                            placeholder ="Email"
                            type="text" 
                            name="username"
                            required
                            value={inputs.username || ""} 
                            onChange={handleChange}
                        />
                        </label>
                        <label>
                            <input 
                            placeholder ="Lösenord"
                            type="password"
                            name="psw"
                            required
                            value={inputs.psw || ""} 
                            onChange={handleChange}
                            />
                            </label>
                            <input type="submit" value="Logga in" />
                            <Delimiter></Delimiter>
                            <input type="submit" value="Skapa konto" />
                    </form>

                </SignIn>

                <SignIn>
                    <form className = "register">

                         <button onClick={handleSubmit2} value="Skapa konto" />
                    </form>

                </SignIn>

            </Content>
        </Wrapper>
    )
}

export default FormLogin
