import styled from 'styled-components';

export const Wrapper = styled.div `
	display:flex;
	padding-top: 10px;
	font-size: 1em;
	height: 100vh;
	width: 100vw;
	text-align: center;

`;

export const Content = styled.div `
	margin: auto;
	width: 50%;
	height: 100%;
	background-color: #3776ab;
	padding-top:2em;
	position:relative;
    .form {
      padding-bottom: 5em;
    }

`;



export const SignIn = styled.div `
* {
	display:flex;
	flex-direction: column;

}


/* Full-width input fields */
input[type=text], input[type=password] {
	flex:1;
	width: 10%;
	min-width: 10em;
	padding: 15px;
	margin: 5px 0 22px 0;
	border: none;
	background: #f1f1f1;
	text-align: center;
	font-size: 1.1em;
}
input[type=submit] {
	flex:1;
	width: 10%;
	min-width: 10em;
	margin: 5px 0 22px 0;

}

input[type=text]:focus, input[type=password]:focus {
	background-color: #ddd;
	outline: none;
}

.register {
	align-items: center;
	border: 2px solid red;
}



`;
