                        //     setPopupInfo({	
                        //         showBike: false,
                        //         showCity: true,
                        //         showLocation: false,
                        //         content: cityCoords
                        //     })
                        // });
                        cityname.setMap(map);
                        setLoadStationObject(oldArray => [...oldArray, cityname]);

                        })
                    }
                    return {LoadStationObject};


            } catch (error) {
                console.log(error)
            }

}
}, []);



}
export default useLoadStationsForMap;


































