import {useState, useEffect} from "react";
import Api from "../api";


export const useFetchAUser = (id) => {

    const [auser, setAUser] = useState([]);
    const [userLoading, setLoading] = useState(false);
    const [message, setMessage] = useState("Loading...");


    const fetchData = async (id) => {
        let data;
        try {
            setLoading(true)
            const res = await Api.getAUser(id)
            data = res.data;

            if(data.trips.length > 0) {
                setState(data.trips)
            } else {
                setMessage("Ingen resa gjord")
            }

        } catch (error) {
            setMessage("Något gick fel vid hämtningen från servern")
        }
        setLoading(false)
    }  

    // initial render
    useEffect(() => {
        fetchData(id);
    },[]);

    return {state, loading, message}
}


